import React from 'react';
import {Button, Grid} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import jeans from '/home/prashant/myapp/src/Assets/Images/jeans.jpg';

const useStyles = makeStyles(() => ({
	gridCss: {
		fontSize: '20px',
		fontWeight: 900,
		background: '',
		color: 'white',
		marginTop: '2rem',
	},
	contentCss: {
		position: 'absolute',
		bottom: 200,

	},
	imgCss: {
		width: '100%',
		height: 400,
		zIndex: -1,
	},
	btnCss:{
		  color: 'white',
			backgroundColor: 'red',
			fontWeight: 500,
			border: '2px solid red',
			bordeRadius: 5,
			cursor: 'pointer',
	 }
}));

const MensDetails = () => {
	const classes = useStyles();
  return <Grid  container item spacing={1} className={classes.gridCss} justify='center'>
	      <Grid item xs={12}><img className={classes.imgCss} src={jeans}/></Grid>
				<Grid item xs={6} className={classes.contentCss}>End of season sale</Grid>
				<Grid item xs={6} className={classes.contentCss}>Upto 50% jeans collection</Grid>
				<Grid item xs={4}><Button className={classes.btnCss} size='large'>View Collection</Button></Grid>
		</Grid>
}
export default MensDetails;
