import styled from "styled-components";

export const BagShopwrapper = styled.div`

   
     width:${props => props.size==="Large" ? "100%" : "50%"};
  
        
`;
export const BagShop = styled.img`

    
    width:100%;
    height:100%;
  
        
`;
export const BagShopButton = styled.div`

    position: relative;
    left: 0px;
    bottom: 130px;
        
`;
