import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

import slider1 from '../../Assets/Images/slider1.jpg';
import slider2 from '../../Assets/Images/slider2.jpg';
import {SliderImg} from './Style';
import MensDetails from './Button';
import { makeStyles } from '@material-ui/core/styles';
import {Grid} from '@material-ui/core';
import ArrowBackIosIcon from '@material-ui/icons/ArrowBackIos';
import ArrowForwardIosIcon from '@material-ui/icons/ArrowForwardIos';

const useStyles = makeStyles(() => ({
   divClass: {
		display: 'block',
		width: 55,
		height: 60,
		backgroundColor: 'white',
		'&:hover':{
			color: 'white',
			backgroundColor: 'red',
			cursor: 'pointer',
			transition: '.2s',
		}
	 },
	 contentCss:{
		 fontSize: 90,
		 fontWeight: 500,
		 color: 'white',
		 zIndex: 2,
		 position: 'absolute',
	 }
  }));

function Arrow(props) {
  let className = props.type === "next" ? "nextArrow" : "prevArrow";
	className += " arrow";
	const classes = useStyles();
	console.log(props.type, 'kll');
  const char = props.type === "next" ? <ArrowForwardIosIcon/> : <ArrowBackIosIcon/>;
  return (
    <Grid className={`${className} ${classes.divClass}`} justify='center' alignItems='center' onClick={props.onClick}>
      {char}
    </Grid>
  );
};
 
export default () => {
  var settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
		slidesToScroll: 1,
		nextArrow:<Arrow type="next" />,
    prevArrow:<Arrow type="prev" />,
	};
	const classes = useStyles();
  return (
    <Slider {...settings}>
      <div>
       <SliderImg src={slider1}/>
      </div>
      <div>
       <img src={slider2}/>
			 <Grid className={classes.contentCss}>Men's Casusal</Grid>
      </div>
    </Slider>
  );
};
