import React from 'react';
import {Button, Grid} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import jeans from '/home/prashant/myapp/src/Assets/Images/jeans.jpg';

const useStyles = makeStyles(() => ({
	gridCss: {
		marginTop: '2rem',
		position: 'absolute',
		zIndex:200,
	},
	contentCss: {

	},
	imgCss: {
		width: '100%',
		height: 400,
		position: 'relative',
		zIndex:-1,
	},
	btnCss:{
		  color: 'white',
			backgroundColor: 'red',
			fontWeight: 500,
			border: '2px solid red',
			bordeRadius: 5,
			cursor: 'pointer',
	 }
}));

const MensDetails = () => {
	const classes = useStyles();
  return <Grid  container item spacing={1} justify='center'>
	      <img className={classes.imgCss} src={jeans}/>
				<Grid container item spacing={2} className={classes.gridCss}>
					<Grid item xs={6} className={classes.contentCss}>End of season sale</Grid>
					<Grid item xs={6} className={classes.contentCss}>Upto 50% jeans collection</Grid>
					<Grid item xs={4}><Button className={classes.btnCss} size='large'>View Collection</Button></Grid>
				</Grid>
		</Grid>
}
export default MensDetails;
